INSERT INTO public.udc (udc_abb, description) VALUES ('821', 'Художественная литература на отдельных языках Подразделения класса 821 получаются присоединением к коду класса общ. определителей языка (Табл. Ic) с заменой знака равенства на точку. Ниже приведены только лишь отдельные наиболее важные примеры. Литература на диалектах обозначается спец. определителем ''282 и соответствующими его подразделениями. Для обозначения литературы в отдельных регионах и странах используются общ. определители места (4/9), добавляемые к коду класса 821 или к коду соответствующего языка Например: 821(494) Швейцарская литература в целом 821.133.1(494) Швейцарская литература на французском языке 821.133.1''282(494) Литература на швейцарском диалекте французского языка 821.1/.9 подразделять как =1/=9') ON CONFLICT (udc_abb) DO NOTHING;
