INSERT INTO public.udc (udc_abb, description) VALUES ('94(470+571)', 'История Российского государства в целом При дальнейшем обозначении конкретных периодов и исторических дат российской истории для удобства (если нет конкретной необходимости) рекомендуется использовать сокращенное обозначение регионального аспекта, т.е. 94(470) История азиатской части России → 94(571) Отдельные периоды российской истории, а также конкретные исторические события подразделять с помощью общих определителей времени "..." Например: 94(470)".../1150" История России в древности (до середины XII в.) 94(470)"980/1015" Период правления князя Владимира Святого, 980– 1015 гг. 94(470)"1150/15" История России в Средние века (с середины XII до конца XVI в.) 94(470)"1238/1480" Монголо-татарское иго, 1238–1480 гг. 94(470)"16/18" История России Нового времени (XVII-XIX в.) 94(470)"1613/1645" Период правления царя Михаила Романова, 1613– 1645 гг. 94(470)"1812" Отечественная война 1812 г. 94(470)"19/..." История России Новейшего времени (с начала ХХ в.) 94(470)"1894/1917" Правление Николая II, 1894–1917 гг. 94(470)"1904/1905" Русско-японская война 1904–1905 гг. 94(470)"1917" Февральская революция 1917 г. Временное правительство 94(470)"1941/1945" Великая Отечественная война 1941–1945 гг. 94(470)"1991..." История Российской Федерации с 1991 г. Отдельные периоды истории Советского государства можно обозначать как в контексте истории Российского государства в целом, так и выделяя отдельный период в классе 94(47), в зависимости от целей и взглядов систематизатора. Таким образом, историю СССР, в том числе и историю Советской власти с ноября 1917 г., можно обозначать 94 (47+57), сокращенно 94(47) → 94(57) История азиатской части бывшего СССР Историю государств, возникших на территории бывшего СССР, обозначать с помощью общих определителей места Например: 94(474.5) История Литвы 94(476) История Республики Беларусь 94(477) История Украины 94(479.25) История Армении 94(574) История Казахстана 94 (575.4) История Киргизии → 94(5) История Востока') ON CONFLICT (udc_abb) DO NOTHING;
